import React from 'react';

const ListItem = ({ listItems, handleRemoveItem }) => {
    const deleteItem = (index) => () =>
    setListData((items) => items.filter((_, i) => i !== index));

  return (
    <ul>
    {
      listItems.map(listItem => {
        const { id, img, task } = listItem;
        return (
            <li name={id} className="list" key={id}>
                <img src={img} alt={task} />
                <p>
                    {task}
                </p>
                <button className='remove-btn' onClick={() => handleRemoveItem(id)}>Remove</button>
            </li>
        )
      })
    }
    </ul>
  );
}

export default ListItem;
