import React, {useState} from 'react';
import ListItem from './ListItem';
import data from '../../data.json';

const List = () => {

  const [listData, setListData] = useState(data);
  const [updateListData, setUpdateListData] = useState(data);

  const handleRemoveItem = (e) => {
    const key = e.target.getAttribute("key")
    setUpdateListDataa(updateListData.filter(item => item.id !== key));
   };

  return (
    <main className="list">
      <section>
        <h2>{listData.length} things to do during the week</h2>
          <ListItem listItems={listData} 
            handleRemoveItem={() => handleRemoveItem} />
        <button onClick={() => setListData([])}>Clear all</button>
      </section>
    </main>
  );
}

export default List;
